(function() {
  const categoriesSwiper = document.getElementById('categoriesSwiper');
  const nextBtn = document.getElementById('categoriesSwiperNext');
  const prevBtn = document.getElementById('categoriesSwiperPrev');
  const maxScrollLeft = categoriesSwiper.scrollWidth - categoriesSwiper.clientWidth;
  const perScroll = 280;

  nextBtn.addEventListener('click', function(e) {
    e.preventDefault();
    let nextPosition = categoriesSwiper.scrollLeft + perScroll;
    if (nextPosition > maxScrollLeft) {
      nextPosition = maxScrollLeft
    }
    categoriesSwiper.scrollLeft = nextPosition
  })

  prevBtn.addEventListener('click', function(e) {
    e.preventDefault();
    let nextPosition = categoriesSwiper.scrollLeft - perScroll;
    if (nextPosition < 0) {
      nextPosition = 0
    }
    categoriesSwiper.scrollLeft = nextPosition
  })

  categoriesSwiper.addEventListener('scroll', function() {
    if (categoriesSwiper.scrollLeft === 0) {
      prevBtn.classList.add('hide')
    } else {
      prevBtn.classList.remove('hide')
    }

    if (categoriesSwiper.scrollLeft === maxScrollLeft) {
      nextBtn.classList.add('hide')
    } else {
      nextBtn.classList.remove('hide')
    }
  })

})();

