(function(){
  const elementReady = (parent, selector) => {
    return new Promise(resolve => {
      let el = parent.querySelector(selector)
      if (el) {
        resolve(el)
      }

      new MutationObserver((mutationRecords, observer) => {
        Array.from(parent.querySelectorAll(selector)).forEach(element => {
          resolve(element)

          observer.disconnect()
        })
      }).observe(parent, {
        childList: true,
        subtree: true,
      })
    })
  }

  window.addEventListener('DOMContentLoaded', function() {
    elementReady(document.querySelector('body'), '#omnisend-dynamic-container').then(item => {
      const omnisendContainer = document.querySelector('#omnisend-pixel-container')
      const images = omnisendContainer.querySelectorAll('img')
      images.forEach(function(img){
        img.width = '1'
        img.height = '1'
      })
      item.addEventListener('mousedown', function(e){
        const target = e.target
        if (e.target.value === 'Send My Coupon') {
          onFormSubmit(target.closest('form'))
        }
      })
    })
  })

  function onFormSubmit(form) {
    if (!form) return
    const email = form.querySelector('input[name="omnisend-email"]')
    if (!email) return

    if (validateEmail(email.value)) {
      fbq('track', 'Lead');
      ga('send', 'event', 'Newsletter form', 'submit', 'omnisend_exit');
    }
  }

  function validateEmail(email) {
    const re = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{1,}))$/;
    return re.test(email);
  }
})()

