(function () {
  function waitForElement(selector, trigger, delayInterval, delayTimeout) {
    var interval = setInterval(function () {
      if (
        document &&
        document.querySelector(selector) &&
        document.querySelectorAll(selector).length > 0
      ) {
        clearInterval(interval);
        trigger();
      }
    }, delayInterval);
    setTimeout(function () {
      clearInterval(interval);
    }, delayTimeout);
  }

  function init() {
    document.querySelectorAll('.custom-content .rte h2').forEach(function(h2){
      if (h2.innerText.toLowerCase().indexOf('b-corp') > -1) {
        h2.closest('.shopify-section').classList.add('pg-hidden');
      }
    });
  }

  waitForElement('.custom-content .rte h2', init, 50, 10000);
})();

