  /**** CUSTOM RADIO BUTTON ****/
  .awt-style-1 .bundleapp-wrapper input[type='radio'],
  .awt-style-2 .bundleapp-wrapper input[type='radio'] {
      /* Add if not using autoprefixer */
      -webkit-appearance: none;
      /* Remove most all native input styles */
      appearance: none;
      /* For iOS < 15 */
      background-color: #fff;
      /* Not removed via appearance */
      margin: 0 10px 0 0;
      color: currentColor;
      width: 20px;
      height: 20px;
      border: 1px solid currentColor;
      border-radius: 50%;
      /*     transform: translateY(-0.075em); */
      display: grid;
      place-content: center;
      min-height: 0 !important;
      min-width: 20px;
      outline: none;
      padding: 0;
  }
  .awt-style-1
      .bundleapp-wrapper
      input[type='radio']:focus
      .awt-style-2
      .bundleapp-wrapper
      input[type='radio']:focus {
      /* remove outline */
      outline: none;
  }
  .awt-style-1 .bundleapp-wrapper input[type='radio']::before,
  .awt-style-2 .bundleapp-wrapper input[type='radio']::before {
      content: '';
      width: 10px;
      height: 10px;
      border-radius: 50%;
      transform: scale(0);
      transition: 120ms transform ease-in-out;
      background-color: var(--awt-radio-button-background);
      transition: background-color 0.3s;
  }
  .awt-style-1 .bundleapp-wrapper input[type='radio']:checked::before,
  .awt-style-2 .bundleapp-wrapper input[type='radio']:checked::before {
      transform: scale(1);
  }
