$(document).on("click", ".next-link > a", function(e) {
	e.preventDefault();
	$.ajax({
		type: "GET",
		url: $(this).attr("href"),
		success: function(a) {
			if(a){
				$("#newsboxHP2").append(a);
				$(".next-link").hide();
				$(".next-link:last").show();
			}
		}
	});
});


$(document).ready(function() {
	var activeTab = 1;
	var totalTabs = 3;

	$("#news-popular-next-tab").click(function() {
		if (activeTab >= totalTabs) return false;
		activeTab++;
		$("#news-popular-tabs .tab").hide();
		$("#news-popular-tabs #tabs-" + activeTab).show();
		return false;
	});
	$("#news-popular-prev-tab").click(function() {
		if (activeTab <= 1) return false;
		activeTab--;
		$("#news-popular-tabs .tab").hide();
		$("#news-popular-tabs #tabs-" + activeTab).show();
		return false;
	});
});

