		 */
		isEmpty: function(value)
		{
			var Type = window.scUtils.Type;


			return (value === null) || (value === undefined) || value === '' || (Type.isArray(value) && value.length === 0);
		},


		/**
		 * （クラスメソッド）
		 * 引数で指定された値がundefinedかどうかテストする。
		 * 
		 * @access public
		 * @param  {Mixed}  value  テストしたい値を指定する。
		 * @returns {Boolean}  undefinedの場合にはtrueが、それ以外の場合にはfalseを返す。
		 */
		isUndefined: function(value)
