			}


			isHasOwnPropOnly = (typeof(isHasOwnPropOnly) === "undefined" ? true : (isHasOwnPropOnly ? true : false));
			var count = 0;
			for( var prop in object ){
				if( isHasOwnPropOnly && !object.hasOwnProperty(prop) ){
					continue;
				}
				count++;
			}

			return count;
		},


		/**
		 * （クラスメソッド）
