			//overrideObjectの内容で上書きする
			//
			for( var prop in overrideObject ){
				if( !overrideObject.hasOwnProperty(prop) ){
					continue;
				}

				if( !(prop in newObject) || isForce ){
					newObject[prop] = overrideObject[prop];
					continue;
				}

				var isEmpty = false;
				for( var i = 0; i < emptyValues.length; i++ ){
					if( emptyValues[i] === newObject[prop] ){
						isEmpty = true;
						break;
					}
				}

				if( isEmpty != true ){
					continue;
				}

				newObject[prop] = overrideObject[prop];
			}

			return newObject;
		},


		/**
		 * （クラスメソッド）
		 * ピリオド区切りで階層構造を表した文字列を元に、連想配列内を走査して、指定されたキー値に値を設定する。
		 * 階層構造を表したキーでの階層構造がない場合には、自動的にオブジェクトを生成して階層を生成する。
		 * 
		 * @param {Object} target  値を設定すべき連想配列を指定する。 
		 * @param {String} hiers   ピリオド区切りで表されたキー階層を指定する。この指定された階層に値が設定される。
		 *                         対象となるキーが連想配列内に存在しない場合には自動的に作成される。 
		 * @param {Mixed}  value   設定すべき値を指定する。 
		 */
		setHierValue: function(target, hiers, value){
			var Type = window.scUtils.Type;

			//
			//解析ターゲットが空指定の場合には一旦、空オブジェクトに置きなおす。
			//そもそもそのようなケースが発生すること自体間違い。
			//
			target = target || {};


			//
			//指定される階層文字列（配列）が空の場合、何もしようがないのでvalueをそのまま返す。
			//
			if( !hiers ){
				return target = value;
			}


			//
			//指定される階層が文字列の場合、「.」区切りで分割して配列化する。
			//書式として許容されるのは以下のようなもの
			//なおxdmの階層（キー）において「.」を含むケースはないはずなのでそのケースはこのロジックでは対応しない。
			//　aaaa.bbb.ccc・・・
			//　aaaa.[].ccc・・・
			//　aaaa.[1].ccc・・・
			//
			if( Type.isString(hiers) ){
				hiers = hiers.match( /(\[\d*\]|[^\.]+)/g );
			}


			//
			//階層指定が配列でなければ何もしない。
			//valueをそのまま返す。
			//
			if( !Type.isArray(hiers) ){
				return target = value;
			}


			//
			//階層指定に従い、オブジェクト内容を走査していく。
			//当該キーや配列要素がない場合には動的に作っていく。
			//
			var o = target, m = null, key = "", isAfterTypeArray = false;
			for( var i = 0; i < hiers.length - 1; i++ ){
				m   = hiers[i].match( /\[(\d*)\]/ );
				key = ( !m ? hiers[i] : (m[1] ? parseInt(m[1]) : -1) );


				//一つ後の要素を先読みして配列指定か否かを調べる
				isAfterTypeArray = hiers[i + 1].match(/\[\d*\]/) ? true : false;


				//階層中にはオブジェクトまたは配列しかないはず（でないと入れ子にできないので）
				if( Type.isObject(o) ){
					if( !(key in o) ){
						//一つ後の要素が配列であれば空配列を設定する。そうでなければ空オブジェクトを設定する
						o[ key ] = ( isAfterTypeArray ? [] : {} );
					}
				}
				else if( Type.isArray(o) ){
					//一つ後の要素が配列であれば空配列を設定する。そうでなければ空オブジェクトを設定する
					if( key == -1 ){
						o.push( isAfterTypeArray ? [] : {} );
						key = o.length - 1;
					}
					else if( o[key] === undefined ){
						o[ key ] = ( isAfterTypeArray ? [] : {} );
					}
				}
				else{
					//下記の行でエラーとなるはず。				
				}


				//次の値を取得
				o = o[ key ];
			}


			//
			//最後の階層指定に対する処理。このケースでは引数で指定された値をその階層に直接代入する。
			//
			m = hiers[ hiers.length - 1 ].match( /\[(\d*)\]/ );
			key = ( !m ? hiers[i] : (m[1] ? parseInt(m[1]) : -1) );
			if( key == -1 ){
				o.push( value );
			}
			else if( o[key] === undefined ){
				o[ key ] = value;
			}

			return target;
		}
	};


	/**
	 * 配列関係ユーティリティクラス
	 */
	window.scUtils.Array = {
