	//ページトラッキングか、カスタムリンク送信と思われるがlinkTypeが設定されている場合のみ実行
	//
	if( (isLinkTracking == false && !s.linkType && !s.clickObject) || (isLinkTracking == true && s.linkType) ){
		//
		//Analytics計測向けのXDMへのマッピング処理
		//
		if( scUtils && scUtils.AnalyticsUtil && typeof(scUtils.AnalyticsUtil.mappingXdm) == "function" ){
			scUtils.AnalyticsUtil.mappingXdm( content, trackingData, isLinkTracking );

		}


		//
		//Target向けのXDMへのマッピング処理（旧レガシー連携相当処理のものでmboxパラメータ相当で計測内容を送信する）
		//
		if( scUtils && scUtils.TargetUtil && typeof(scUtils.TargetUtil.mappingXdmForLegacy) == "function" ){
			scUtils.TargetUtil.mappingXdmForLegacy( content, s, isLinkTracking );
		}
	}

	return true;
}


/**
 * Targetリクエストのための処理を実行
 * 
 * @param {Object}   content   xdmオブジェクトが指定される。
 * @param {Object}   s         Appmeasurementの模倣クラスに対するインスタンスが設定される。
 * @returns {Boolean}  trueの場合にはビーコン送信を実施、falseの場合にはビーコン送信抑止
 */
function requestTargetInformation(content, s){
	var scUtils = window.scUtils;

	var isSendBeacon = false;
	var isSendGlobalMbox = false;

	//
	//scUtils.TargetUtilが存在しなければTarget対応しないので何もしない。
	//
	if( !scUtils.TargetUtil ){
		return isSendBeacon;
	}


	//
	//ページビューの場合のみ実行
	//
	var eventType = scUtils.Object.getHierValue(content, "xdm.eventType"); 		
	if( eventType == "web.webpagedetails.pageViews" || eventType == "web.webinteraction.linkClicks" ){
		//
		//自動的にtarget-global-mbox相当のコールを実施する。
		//
		isSendGlobalMbox = scUtils.TargetUtil.autoFireGlobalMbox( content );	
	}


	//
	//テンポラリデータ中に指定があればそれを採用
	//
	var renderDecisions     = null;
	var decisionScopes      = null;
	var targetTemporaryData = scUtils.Object.getHierValue( content, "data.__temporary.target" );
	if( typeof(targetTemporaryData) == "object" ){
		delete content.data.__temporary;

		renderDecisions = ( typeof(targetTemporaryData.renderDecisions) == "boolean" ? targetTemporaryData.renderDecisions : null );
		decisionScopes  = ( scUtils.Type.isArray(targetTemporaryData.decisionScopes) ? targetTemporaryData.decisionScopes  : null );
	}
	else{
		targetTemporaryData = {};
	}

	if( renderDecisions !== null && !("renderDecisions" in content) ){
		content.renderDecisions = renderDecisions;
	}

	if( decisionScopes != null ){
		if( !("decisionScopes" in content) ){
			content.decisionScopes = decisionScopes;
		}
		else{
			decisionScopes.forEach(function(scope){
				content.decisionScopes.push( scope );
			});	
		}
	}


	//
	//最終的に送信するか決定する
	//
	isSendBeacon = ( ("decisionScopes" in content) && content.decisionScopes.length > 0 ? true : false )


	//
	//__view__を送信しない場合において、mboxパラメータの追加処理を実施
	//
	if( isSendBeacon && !isSendGlobalMbox ){
		var mboxParams = Object.assign( 
			{}, 
			scUtils.TargetUtil.getDefineTargetPageParams( "__not_view__" ), 
			{}
		);


		//
		//mboxパラメータ追加
		//
		Object.keys(mboxParams).forEach(function (key) {
			scUtils.Object.setHierValue( content, "data.__adobe.target." + key, mboxParams[key] );
		});
	}


	return isSendBeacon;
}


/**
 * 予めページ内に設定されている、ちらつき防止用のstyleタグを削除する。
 * 
 * @param {string}  id  ちらつき防止用のstyleタグに対するid属性を指定する。 
 */
function removePreHideElement(id){
	var styleEl  = window.document.getElementById( id );
	var parentEl = (styleEl ? styleEl.parentNode : null);
	if( parentEl ){
		parentEl.removeChild( styleEl );
	}
}


/**
 * 計測終了処理を実施
 * 
 * @param {Object} content  xdmに対するオブジェクトを指定する 
 * @param {Object} s        sオブジェクトを指定する 
 */
function terminate(content, s){
	if( !s ){
		return;
	}

	//
	//sオブジェクト内のクリア処理
	//
	s.abort = s.supplementalDataID = s.timestamp = s.pageURLRest = s.linkObject = s.clickObject = s.linkURL = s.linkName = s.linkType = window.s_objectID = s.pe = s.pev1 = s.pev2 = s.pev3 = s.e = s.lightProfileID = 0;
}


/**
 * ユニークIDを生成する。
 *
 * @access private
 * @param  {integer}  len     生成するユニーク値の長さを指定する。
 * @param  {string}   chars   使用する文字候補を文字列として指定する。
 * @return {string}  生成されたユニーク値を返す。
 */
function createUniqId(len, chars){
	var tbl = chars.split( '' );

	var value  = '';
	for( var i = 0; i < len; i++ ){
		value += tbl[ Math.floor(Math.random() * tbl.length) ];
	}

	return value;
}


/**
 * scUtilsオブジェクトに対して、WEBSDK対応処理に必要やユーティリティメソッド群を追加する。
 */
function doProcess(){
	if( !window.scUtils ){
		window.scUtils = {};
	}

	const PRE_HIDE_STYLE_ID          = "alloy-prehiding";
	const PRE_HIDE_STYLE_ID_FOR_ATJS = "at-body-style";
	const LOG_PREFIX                 = "[SC DEBUG] ";
	const SDID_CHARS                 = "0123456789ABCDEF";


	/**
	 * Launchより組織IDを取得して「AMCV」Cookieがあればそこからmidを取得する。
	 */
	var orgId = scUtils.Object.getHierValue( window._satellite, "company.orgId" ) || "";
	var ecid  = "";
	if( orgId ){
		var amcv = window._satellite.cookie.get("AMCV_" + decodeURIComponent(orgId) ) || "";
		var m    = amcv.match( /(?:^|\|)MCMID\|([^\|]+)/ );
		ecid = (m ? m[1] : "");
	}


	/**
	 * 組織IDやECIDなどの情報格納用変数
	 */
	var identityInformation = {
		orgId: orgId,
		ecid:  ecid
	};


	/**
	 * SDID格納用の変数
	 */
	var supplementalDataID = "";


	/**
	 * デバッグモードをlocalStorageより取得する。 
	 */
	var debugMode = (function(){
		var mode = window.localStorage.getItem("sc_debug_log") || "";

		switch( mode.toLowerCase() ){
			case "true":
			case "log":
				return "log";

			case "table":
				return "table";

			default:
				return "";
		}
	}());


	//
	//sendEvent実行後のコールバック関数格納用バッファ
	//
	var afterCallbackTasks = [];


	/**
	 * Web sdk関係ユーティリティクラス
	 */
	window.scUtils.WebsdkUtil = {
		/**
		 * JavaScriptのバージョン情報を取得する。
		 * 
		 * @returns {String}  JavaScriptのバージョン番号を返す。 
		 */
		getJavaScriptVersion: function(){
			var b = new Date, d, f, l = "1.2";

			if( b.setUTCDate ){
				l = "1.3";
				if( (0).toPrecision ){
					l = "1.5";
					b = [];
					if( b.forEach ){
						l = "1.6";

						f = 0;
						d = {};
						try {
							f = new Iterator(d),
							f.next && (l = "1.7",
							b.reduce && (l = "1.8",
							l.trim && (l = "1.8.1",
							Date.parse && (l = "1.8.2",
							Object.create && (l = "1.8.5")))))
						} catch (r) {}					
					}
				}
			}

			return l;
		},


		/**
		 * Cookieが有効かどうか検査する。
		 * 
		 * @returns {Boolean}  Cookieが有効な場合にはtrueを、それ以外はfalseを返す。
		 */
		isCookieEnabled: (function(){
			const COOKIE_NAME = "s_websdk_cc";

			_satellite.cookie.set( COOKIE_NAME, "t" );
			var isEnabled = ("t" == _satellite.cookie.get(COOKIE_NAME) );

			try{
				_satellite.cookie.remove( COOKIE_NAME )
			}
			catch(e){};

			return function(){
				return isEnabled;
			}
		})(),


		/**
		 * 仮想的に作成したSDIDを返す。
		 * 
		 * @returns {String}  SDIDを返す。onBeforeEventSend()メソッドコール前には空文字列が返るので注意が必要。
		 */
		getSupplementalDataID: function(){
			return supplementalDataID; 
		},


		/**
		 * sendEventコマンド後に処理が成功した場合に呼び出したいコールバック関数を登録する。
		 * 
		 * @param {String}   name        タスク名を指定する。この名称がデバッグログに表示される。
		 * @param {Boolean}  isOnceOnly  1回のみ実行する場合にはtrueを指定する。ビーコン送信タイミングで何度もタスク実行する場合にはfalseを指定する。
		 * @param {Function} fn    コールバックすべき関数を指定する。 
		 */
		registerAfterSendBeaconTasks: function(name, isOnceOnly, fn){
			afterCallbackTasks.push( {
				name:       name,
				isOnceOnly: isOnceOnly,
				fn:         fn
			} );
		},


		/**
		 * sendEventコマンド後に実行すべきコールバック関数を実行する。
		 * このメソッドは、scUtils.AnalyticsUtil.sendBeacon()から呼び出されることを想定している。 
		 * 
		 * @param {Object}  result   sendEventコマンド実行後のレスポンス内容を表すオブジェクト
		 * @param {Object}  content  今のところnullのみ
		 */
		runAfterSendBeaconTasks: function(result, content){
			var scUtils = window.scUtils;


			//
			//コールバックする関数が0であれば何もしない
			//
			if( afterCallbackTasks.length < 1 ){
				return;
			}


			//
			//順次実行を行う。
			//
			scUtils.WebsdkUtil.showConsoleGroupCollapsed( "run after send tasks." );


			var promise = Promise.resolve();
			var newAfterCallbackTasks = [];
			for( var i = 0; i < afterCallbackTasks.length; i++ ){
				var task = afterCallbackTasks[ i ];

				scUtils.WebsdkUtil.showConsoleDebugLog( "run task. #%d [%s]", i + 1, task.name );
				promise = promise.then((function(){
					var func = task.fn;
					return function(){
						return func( result, content );
					}
				})()).catch(function(error){
					scUtils.WebsdkUtil.showConsoleErrorLog( "scUtils.WebsdkUtil.runAfterSendBeaconTasks Error: ", error );
				});

				if( task.isOnceOnly != true ){
					newAfterCallbackTasks.push( task );
				}
			}
			scUtils.WebsdkUtil.showConsoleGroupEnd();
			afterCallbackTasks = newAfterCallbackTasks;


			/*
			var newAfterCallbackTasks = [];
			scUtils.WebsdkUtil.showConsoleGroupCollapsed( "run after send tasks." );
			for( var i = 0; i < afterCallbackTasks.length; i++ ){
				var task = afterCallbackTasks[ i ];

				scUtils.WebsdkUtil.showConsoleDebugLog( "run task. #%d [%s]", i + 1, task.name );	
				task.fn( result, content );

				if( task.isOnceOnly != true ){
					newAfterCallbackTasks.push( task );
				}
			}
			scUtils.WebsdkUtil.showConsoleGroupEnd();

			afterCallbackTasks = newAfterCallbackTasks;
			*/
		},


		/**
		 * sendEventを発行する。
		 * このメソッドは、sオブジェクトのt()メソッドもしくはtl()メソッドから呼び出されることを想定している。
		 * 
		 * @param {Object} data  テンポラリデータとして指定したい値（sオブジェクトの計測値）を指定する。 
		 * @param {Boolean} isLinkTrakcing  リンクトラッキングの場合にはTrueを、それ以外はfalseを指定する。 
		 */
		sendBeacon: function(data, isLinkTracking){
			var scUtils = window.scUtils;


			//
			//decisionScopesの決定を行う。
			//Target対応を行っている場合に、ページトラッキングの場合とそれ以外の場合に場合分けする。
			//
			var decisionScopes = undefined;
			if( window.scUtils.TargetUtil ){
				decisionScopes = (!isLinkTracking ? [ "__view__" ] : [ "SiteCatalyst: event" ]);
			}


			alloy( "sendEvent", {
                documentUnloading: isLinkTracking ? true : false,
				data: {
					"__temporary": {
						analytics: scUtils.Type.isObject(data) ? data : {}
					}
				},
				xdm: {
					eventType: (isLinkTracking ? "web.webinteraction.linkClicks" : "web.webpagedetails.pageViews")
				},
				decisionScopes: decisionScopes
			} ).then(function(result){
				var scUtils = window.scUtils;


				//
				//ちらつき防止のstyleの除去を行う。
				//
				removePreHideElement( PRE_HIDE_STYLE_ID );
				removePreHideElement( PRE_HIDE_STYLE_ID_FOR_ATJS );

				//
				//レスポンス後のコールバック関数の登録を実行する
				//
				scUtils.WebsdkUtil.runAfterSendBeaconTasks( result, null );
			}).catch(function(error){
				const scUtils = window.scUtils;
				scUtils.WebsdkUtil.showConsoleErrorLog( "scUtils.WebsdkUtil.sendBeacon Error: ", error );
			});
		},


		/**
		 * alloy()の"sendEvent"コマンドにおいて、ビーコン送信前に呼び出されるコールバック関数内容を定義している。
		 * alloy()のonBeforeEventSendから呼び出される関数。基本的に修正の必要はない。
		 * このメソッドは、WEB SDK ExtensionにおけるonBeforeEventSend項目から呼び出されることを想定している。
		 * 
		 * @param {Object} content  xdmを格納するオブジェクトが指定される。 
		 * @returns {Boolean}  ビーコン送信を抑止される場合にはfalseが、送信される場合にはtrueを返す。
		 */
		onBeforeEventSend: function(content){
			var scUtils = window.scUtils;

			var s = window.s, doPlugins = window.s_doPlugins;
			var isSendBeacon = false


			//
			//sdidを生成
			//
			supplementalDataID = scUtils.String.format( "{0}-{1}", createUniqId(16, SDID_CHARS), createUniqId(16, SDID_CHARS) );


			//
			//Analytics計測処理を実施
			//
			isSendBeacon = doMeareumentAnalytics( content, s, doPlugins );


			//
			//Targetのためのリクエスト処理を実施
			//
			isSendBeacon = ( requestTargetInformation(content, s) ? true : isSendBeacon );


			//
			//ATにおけるインプレッション計測などのためのものであれば送信処理実施
			//
			var eventType = String( scUtils.Object.getHierValue( content, "xdm.eventType") );
			isSendBeacon = ( scUtils.String.startsWith(eventType, "decisioning.") ? true : isSendBeacon );


			//
			//FPID処理
			//
			var fpid = _satellite.getVar("adobe_fpid");
			if( isSendBeacon && fpid ){
				scUtils.Object.setHierValue(content, "xdm.identityMap.FPID", [{
					"id":                 fpid,
					"authenticatedState": "ambiguous",
					"primary":            true
				}]);
			}


			//
			//ログ書き出し
			//
			if( isSendBeacon ){
				scUtils.WebsdkUtil.showConsoleDebugLogForXdm( content );
			}


			//
			//終了処理
			//
			terminate( content, s );
			return isSendBeacon;
		},


		/**
		 * 初回のページトラッキングレスポンス後から使用可能。
		 * ECIDを取得する。
		 * 
		 * @returns {string}  ECID(mid)を返す。
		 */
		getECID: function(){
			return identityInformation["ecid"] || "";
		},


		/**
		 * 初回のページトラッキングレスポンス後から使用可能。
		 * 組織IDを取得する。
		 * 
		 * @returns {string}  組織IDを返す。
		 */
		getOrgId: function(){
			return identityInformation["orgId"] || "";
		},


		/**
		 * デバッグ用の関数。引数のオブジェクト内容の階層を追って、すべての階層を「.」区切りキー値として、そこに設定されている値をコンソール上に表示する。
		 * ただしisShowDebugLog()がtrueを返す場合のみ表示される。
		 * 
		 * @param {Object}  data  コンソール上に表示したいオブジェクトを指定する 
		 */
		showConsoleDebugLogForXdm: function(data){
			var scUtils = window.scUtils;


			//
			//デバッグモードでなければ何もしない。
			//
			if( !scUtils.WebsdkUtil.isShowDebugLog() ){
				return;
			}


			var argLength = arguments.length;
			var prefix    = ( argLength < 2 ? "" : arguments[1] );
			var store     = ( argLength < 3 ? {} : arguments[2] );


			if( !prefix ){
				scUtils.WebsdkUtil.showConsoleGroupCollapsed( "alloy sendEvent" );
			}

			var valueType = "";
			if( typeof(data) == "object" ){
				if( data == null ){
					valueType = "null";
				}
				else{
					valueType = ( Object.prototype.toString.apply(data) == "[object Array]" ? "array" : "object" );
				}
			}
			else{
				valueType = typeof( data );
			}

			switch( valueType ){
				case "object":
					Object.keys(data).forEach(function(key){
						scUtils.WebsdkUtil.showConsoleDebugLogForXdm( data[key], (prefix ? scUtils.String.format("{0}.{1}", prefix, key) : key), store );
					});
					break;

				case "array":
					for( var i = 0; i < data.length; i++ ){
						scUtils.WebsdkUtil.showConsoleDebugLogForXdm( data[i], (prefix ? scUtils.String.format("{0}.{1}", prefix, String(i)) : key), store );
					}
					break;

				case "undefined":
				case "":
					break;

				default:
					switch( scUtils.WebsdkUtil.getDebugMode() ){
						case "table":
							store[ prefix ] = data;
							break;

						case "log":
						default:
							scUtils.WebsdkUtil.showConsoleDebugLog( scUtils.String.format("{0} : {1}", prefix, data) );
							break;
					}
					break;
			}


			if( argLength < 2 ){
				if( scUtils.WebsdkUtil.getDebugMode() == "table" ){
					window.console.table( store );
				}

				scUtils.WebsdkUtil.showConsoleGroupEnd();
			}
		},


		/**
		 * デバッグ用の関数。コンソール上にデバッグログを表示する。内部的にはconsole.debug()が用いられており引数仕様はconsole.debug()と同様である。
		 */
		showConsoleDebugLog: function(/*...*/){
			var scUtils = window.scUtils;


			if( !scUtils.WebsdkUtil.isShowDebugLog() ){
				return;
			}

			var args = [];
			for( var i = 0; i < arguments.length; i++ ){
				args.push( arguments[i] );
			}

			if( args.length > 0 ){
				args[0] = LOG_PREFIX + args[0]
			}

			window.console.debug.apply( window, args );
		},


		/**
		 * デバッグ用の関数。コンソール上にエラーログを表示する。内部的にはconsole.error()が用いられており引数仕様はconsole.error()と同様である。
