/**
 * 引数にて渡された数値を指定の桁数の文字列に変換する。
 * 指定の桁数より小さい場合は、頭に「0」を付けて補完する。
 *
 * @param {number} num 桁数を変換したい数値を指定する。
 * @param {number} max 変換したい桁数を指定する。
 */
function scZeroFormat(num, max){
    var tmp = '' + num;
    while( tmp.length < max ){
        tmp = '0' + tmp;
    }
    return tmp;
}

