/**
 * SPAページにおいて特定の要素を監視し
 * 特定の条件に該当する場合に「spaPageTrack」をコールする。
 * 「spaPageTrack」がコールされるとSPAページ計測用のLaunchルールが発火し、ページ計測が行われる。
 */
function settingSpaPageTrack(){
    if( location.pathname.match(/(^\/simulator\/)|(^\/shop\/online\/counter\/)|(^\/plan-guide\/)|(^\/business\/simulator\/)/) ){
        window.crstep = "none";
        window.nextstep = "none";

        if( $('li[class="current"]')[0] ){
            window.crstep=$('li[class="current"]')[0].innerText.split("\n")[0].replace(/(\s*)/g,"");
        }
        setInterval(function(){
            if( $('li[class="current"]')[0] ){
				window.crstep=$('li[class="current"]')[0].innerText.split("\n")[0].replace(/(\s*)/g,"");
            }
            if( window.crstep != window.nextstep ){
                _satellite.track("pageTrack");
                if( $('li[class="current"]')[0] ){
                    window.nextstep=$('li[class="current"]')[0].innerText.split("\n")[0].replace(/(\s*)/g,"");
                }
            }
        }, 100);
    }
}



