/**
 * 基本的にはAdobe提供外のオリジナルのプラグイン関数類をsオブジェクトに追加する。
 * この関数はdoProcess()より自動的に呼び出される。
 *
 * @param {Object} s  プラグイン関数を追加すべきsオブジェクトが指定される。
 */
function defineOriginalPluginMethod(s){

	/**
	 * s.apl()の複数値対応版。追加及び検査したい値を配列で渡すことで、内部でループしてs.apl()をコールすることで値の追加が可能。
	 *
	 * @param {string}       target        値を追加する変数を指定する。undefinedの場合には内部で空文字に置き換える
	 * @param {string/array} values        配列の場合、指定された値がtargetに存在しなければそれぞれが追加される。文字列の場合は通常のs.apl()と同じ動作になる。
	 * @param {string}       sep           デリミタ文字を指定する。
	 * @param {mixed}        isSensitive   1の場合、大文字小文字を意識しない
	 * @returns {string}  targetに対してvaluesで指定された文字列がユニークな状態で設定された文字列を返す。
	 */
	s.aplMulti = function(target, values, sep, isSensitive){
		var s = this;


		if( !target ){
			target = "";
		}

		if( Object.prototype.toString.apply(values) == "[object Array]" ){
			for( var i = 0; i < values.length; i++ ){
				target = s.apl( target, values[i], sep, isSensitive );
			}
		}
		else{
			target = s.apl( target, values, sep, isSensitive );
		}

		return target;
	}


	/**
	 * 指定されたイベントがs.eventsに存在するか検査する。数値イベントやシリアライズされている場合にも判定が可能である。
	 *
	 * @param {string}  ev  検査したいイベント名を指定する
	 * @returns {booelan}  存在する場合にはtrueを、それ以外の場合にはfalseを返す。
	 */
	s.isExistEvent = function(ev) {
		var s = this;

		if( !s.events ){
			return false;
		}

		return ( s.events.match(new RegExp(ev + "($|\,|\=|\:)")) ? true : false );
	};


	/**
	 * クロスドメイン先の遷移の際に、クリックされた対象のaタグにおけるhref属性に、引き継ぐmidを設定する。
	 * クリックされた対象の遷移先がデータ要素「crossDomain」で指定されており、かつ、自ドメイン以外の場合が引き継ぐmidの付与対象となる。
	 * 付与は「adobe_mc」パラメータとして設定される。
	 *
	 * @param {object}  s sオブジェクトを指定する。
	 */
	s.appendMid = function(s){
        const scUtils = window.scUtils;

        //
        //クリックされた要素がリンクオブジェクトでなければ無視する。
        //
        if( !s.linkObject ){
            return false;
        }

        //
        //URLオブジェクトを作成
        //
        let targetUrl = new URL( s.linkObject["href"] );

        //
        //URLスキーマが「tel」「mailto」「javascript」であれば無視
        //
        if( ["tel:", "mailto:", "javascript:"].includes(targetUrl.protocol) ){
            return false;
        }

        //
        //クロスドメイン対象のドメイン一覧を取得して、遷移先と比較する。
        //遷移先が自ドメインではなく、クロスドメイン先と一致すれば、クロスドメイン用のパラメータを付与する
        //
        let targetDomainParts = targetUrl.hostname.split(".");
        let targetMainDomain = targetDomainParts.slice(targetUrl.hostname.match(/\.(ac|go|co|ne|or|ad|gr|ed)\.jp$/) ? -3 : -2).join(".");
        let currentDomainParts = location.hostname.split(".");
        let currentMainDomain = currentDomainParts.slice(location.hostname.match(/\.(ac|go|co|ne|or|ad|gr|ed)\.jp$/) ? -3 : -2).join(".");
        if( currentMainDomain == targetMainDomain ){
            return false;
        }

        let crossDomains = _satellite.getVar("crossDomain");
        if( !crossDomains.some( elem => {
            var regExp = new RegExp("(^|\\.)" + elem.replace(/\./g, "\\.") + "$");
            return regExp.test(targetUrl.hostname);
        }) ){
            return false;
        }


        //
        //既存で付与されている「adobe_mc」パラメータが存在すれば削除する。
        //
        if( targetUrl.searchParams.has("adobe_mc") ){
            targetUrl.searchParams.delete("adobe_mc");
        }

        //
        //「adobe_mc」パラメータの付与
        //
        targetUrl.searchParams.append("adobe_mc", scUtils.String.format("TS={0}|MCMID={1}|MCORGID={2}", 
            Math.round(new Date().getTime() / 1000),
            scUtils.WebsdkUtil.getECID(),
            encodeURIComponent( scUtils.WebsdkUtil.getOrgId() )
        ));


        //
        //既存で付与されている「fpid」パラメータが存在すれば削除する。
        //
        if( targetUrl.searchParams.has("fpid") ){
            targetUrl.searchParams.delete("fpid");
        }

        //
        //「fpid」パラメータの付与
        //
        let fpid = _satellite.cookie.get("adobe_fpid");
        if( fpid ){
            targetUrl.searchParams.append( "fpid", fpid );
        }


        //
        //クリックしたオブジェクトに展開
        //
        s.linkObject["href"] = targetUrl.href;
        return true;
    }
}

