                $(topWinnerConts[twCont]).find('.js_topwinner_category').parent().prev('.js_top_win_heading_cont').addClass('single')
            } else {
                $(topWinnerConts[twCont]).find('.js_topwinner_category').parent().prev('.js_top_win_heading_cont').removeClass('single')
            }
            let widgetType = $(twTypesElm).attr('data-widget-type');
            let winsType = $(twTypesElm).attr('data-wins-type');
            let wdgtId = $(twTypesElm).attr('data-wdgt-id');
            $(topWinnerConts[twCont]).find('.js_top_win_heading').html($(topWinnerConts[twCont]).find('.js_topwinner_category').first().html());
            getBigWinForHomePage(widgetType, winsType, twcatid, targetId, wdgtId, lobbyUrl);
        }
    }
});

