$(function() {
	$("#submit_comment").click(function() {
		var username = $("#inputUsername").val();
		var email = $("#inputEmailAddress").val();
		var comment = $("#comments").val();
		var pcmsubmit = $("#PCMsubmit").val();
		var response = grecaptcha.getResponse();
// 		comment = comment.replace(/^(\[url=)?(https?:\/\/)?(www\.|\S+?\.)(\S+?\.)?[^.\s]+$\s*/mg, '');
		var dataString = 'username=' + username + '&email=' + email + '&comment='+ comment + '&pcmsubmit=' + pcmsubmit;
		if(username=='' || email=='' || comment==''){
			$('#success').fadeOut(200).hide();
			$('#error').fadeOut(200).show();
		}else if(!valid(email))//call a function which validates email and returns true or false
		{
			$('#success').fadeOut(200).hide();
			$('#error').fadeOut(500).show();
			$('#error').text('Please Enter Valid Email.');
			$('#inputEmailAddress').focus();
			$('#inputEmailAddress').css('border', 'solid 1px red');
		}
		else if(response.length == 0){ 

				// 	alert("enter");
					$('#success').fadeOut(200).hide();
					$('#error').fadeOut(500).show();
					$('#error').text('Please check the captcha checkbox');
		}
		else{
		$.ajax({
				type: "POST",
				url: "submitComments.php",
				data: dataString,
				success: function(data){
					$('#success').fadeIn(200).show();
					$('#error').fadeOut(200).hide();
					$("form").trigger("reset");
				}
			});
		}
		return false;
	});
});


