$(document).ready(function() {
    if(window.location.pathname.match('check-in')){
        $("body").addClass("check-in");
    }
    if(window.location.pathname.match('baggage')){
        $("body").addClass("baggage");
    }
    if(window.location.pathname.match('delta-airlines-cancellation')){
		$("body").addClass("delta");
    }
    if(window.location.pathname.match('delta-flight-change-policy')){
        $("body").addClass("delta");
    }


});

