html, body {
  margin: 0;
  min-height: 100%;
  height: 100%;
}
body {
  background-color: #f4f8fa
}

main {
  padding: 30px;
  max-width: 800px;
  margin: auto;
}

.hsfc-Step {
  background-color: var(--hsf-default-background__background-color, #fff);
