.euCookie,
.agreecookie {
    display: none !important;
}

body.noscroll {
    height: 100%;
    overflow: hidden;
    position: fixed;
    width: 100%;
    top: 0;
}

.pointer {
    cursor: pointer;
}

.consent.hidden,
.consent .hidden {
    display: none !important;
}

.consent ::-webkit-scrollbar {
    width: 5px;
}

.consent ::-webkit-scrollbar-track {
    background: #fff;
}

.consent ::-webkit-scrollbar-thumb {
    background: #FF8800;
}

.consent ::-webkit-scrollbar-thumb:hover {
    background: #EE7700;
}

.consent .clearfix:after {
    content: "";
    clear: both;
    display: table;
}

.consent {
    position: fixed;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    background-color: rgba(0, 0, 0, 0.8);
    z-index: 1000;
    font-size: 16px;
}

.consent a,
.consent a:active,
.consent a:visited {
    color: #07b;
}

.consent > section {
    background-color: #fff;
    display: block;
    width: 1590px;
    max-width: 90%;
    max-height: 95%;
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    overflow-y: auto;
    -webkit-overflow-scrolling: touch;
    scrollbar-color: #FF8800 #fff;
    scrollbar-width: thin;
    overflow-x: hidden;
    border-radius: 15px;
}

.consent > section > div {
    padding: 20px;
}

.consent h2 {
    font-weight: 700;
    margin-bottom: 25px;
    font-size: 30px;
    font-size: 1.875rem;
    color: #555;
    margin-top: 20px;
}

.consent h3 {
    font-size: 28px;
    font-size: 1.777rem;
    margin-bottom: 10px;
    color: #555;
}

.consent h3 label {
    float: right;
}

.consent .ele-checkbox {
    background-color: #888888;
    border: none;
    border-radius: 20px;
    width: 46px;
    height: 22px;
    position: relative;
    display: block;
    font-size: 16px;
    font-size: 1rem;
}
.consent .ele-checkbox,
.consent .ele-checkbox:active{
    outline-offset: none!important;
    outline-color: transparent!important;
    outline-width: 0px!important;
    outline-style: none!important;

}


.consent .ele-checkbox:before {
    left: 14px;
    background-image: url('data:image/png;base64,');
}

.consent .ele-checkbox input {
    display: none;
}

.consent .ele-checkbox .ele-checkbox--state {
    position: absolute;
    top: 3px;
    right: 38px;
    transition: right 0.5s;
}

.consent .ele-checkbox .ele-checkbox--state:after {
    content: "";
    background-repeat: no-repeat;
    background-position: center center;
    background-color: #fff;
    height: 18px;
    border-radius: 20px;
    width: 18px;
    display: block;
    position: absolute;
    top: -1px;
    right: -12px;
    z-index: 1;
}

.consent .ele-checkbox input:checked + .ele-checkbox--state,
.consent-checkbox + .ele-checkbox--state {
    right: 1px;
    transition: right 0.5s;
}

.consent .ele-checkbox input:disabled + .ele-checkbox--state {
    right: 1px;
    opacity: 1;
}

.consent .ele-checkbox input:disabled + .ele-checkbox--state:after {
    content: "";
    background-color: #f5f5f5;
    background-image: url('data:image/png;base64,')
}

.consent__link {
    color: #272CB2;
    cursor: pointer;
}

.consent__link:hover {
    color: #2944CC;
}

.consent .btn--a11y{
    outline: none!important;
    outline-offset: none!important;
    outline-color: transparent!important;
    outline-width: 0px!important;
    outline-style: none!important;
}
.consent .btn--a11y.link--a11y--focused{
    outline-width: 2px!important;
    outline-style: solid!important;
    outline-color: #000!important;
    outline-offset: 4px!important;
}

.consent ul {
    list-style: none !important;
    margin: 0;
}

.consent li:not(:last-child) {
    border-bottom: 1px dotted #ddd;
    margin-bottom: 15px;
}

.consent p {
    margin-bottom: 20px;
}

.consent li p {
    margin-bottom: 0px;
}

.consent .consent__more-less {
    display: inline-block;
    margin-bottom: 10px;
    cursor: pointer;
}



.consent .consent__more-less span:first-child {
    display: inline-block;
}

.consent .consent__btn--sticky {
    margin-top: -10px;
    text-align: right;
    flex-wrap: wrap;
}

.impress {
    margin-left: auto;
    width: 100%;
    text-align: right;
    margin-top: 30px;
    margin-bottom: 0px;
}

.consent #consentLayerOne .consent__btn--sticky {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin: 30px 0;
    padding: 0;
    margin-bottom: 60px;
    width:100%;
}
@media (max-width: 478px) {
