html {
	-webkit-text-size-adjust: 100%; /* Prevent font scaling in landscape while allowing user zoom */
	font-size: 1px;
}

body{
	padding:0px;
	margin:0px;
	overflow-y: auto;
	overflow-x: hidden;
	min-width:300px;
	font-size:12px;
}

ul{
	list-style:none;
	padding: 0px;
}

li{
	margin:0px;
	padding:0px;
}

p{
	margin:0px;
	padding:0px;
}

#xprs{
	overflow:hidden;
}

.item-wrapper{
	margin-left: auto;
	margin-right: auto;
	height:100%;
	width:100%;
	min-height:inherit;
}

.preview-image-holder .image-link{
	min-height:inherit;
	display:inline-block;
}

a{
	text-decoration: none;
	pointer-events:auto;
}

#loading{
	left: 50%;
	position: relative;
}

.container{
	top: 0px;
	width: auto;
	height: 100%;
	left: 0px;
	min-height:inherit;
	position: relative;
}

.element-wrapper .content{
	position: relative;
}

.master.item-box{
	width:100%;
	margin: 0px;
	text-align: center;
}

.master.item-box .stripe-background{
	height: 100%;
	width: 100%;
	min-height: inherit;
	background-repeat: no-repeat;
	background-size: cover;
	background-position:center;
	position: absolute;
	pointer-events:none;
}

.item-box{
	position:relative;
	overflow:visible;
