    .stripe_popup_app {

        position: fixed;
        top: 50%;
    	left: 50%;
    	transform: translateY(-50%) translateX(-50%);
        z-index:99999992;
    }

    #xprs:not(.in-editor) .stripe_popup_app_bg {
        z-index:9999999991;
    }

