function requestStorageAccess() {
  if ('requestStorageAccess' in document === false) {
    return Promise.resolve();
  }

  return document.requestStorageAccess();
}

function loginClicked(e) {
  if (!window.parent || window.parent === window) {
    return;
  }

  const requestToken = "eyJhdWRpZW5jZSI6WyJ1cm46Z2xpdGNoOmNsaWVudCJdLCJpc3N1ZXIiOiJodHRwczovL29wLmdsaXRjaC5jb20iLCJoZWFkZXIiOnsidHlwIjoiSldUIn0sImVuYyI6IkExMjhDQkMtSFMyNTYiLCJhbGciOiJQQkVTMi1IUzI1NitBMTI4S1ciLCJwMmMiOjMwMTksInAycyI6InI2SXBxV1VrRThZQkV1cDJucjdPOGcifQ.FmN80UDUDz5kErpd4yDHGkzESHKCjjCz8i1-ROzFS0PFyhIHly1sYA.7Z1OLu5cTEltKzyXV7lIsQ.v8NQIjXpRXdBWfT5lwbShZFi25Eu00AC65MGdV1nUwCuopYXXm7SvC5km75t0xvSoiFfazOsuvHfJvTpxgwXhz95F-IDKdpkFjB-jUBPiC8ImCS_N2vxD7Y4V9q4lHOoqv6-EojREqEtAjtJo21rFQ.v5KZcy8Bh4kby1psh0E68Q" || null;

  requestStorageAccess()
    .then(() => {
      window.parent.postMessage({ type: "REQUEST_AUTH_TOKEN", requestToken }, "https://glitch.com");
    }, (e) => {
      window.alert("There was an issue logging you in.");
    });

  e.preventDefault();
}

window.addEventListener("message", (e) => {
  if (e.data.type === "AUTH_TOKEN") {
    const url = new URL('/.glitch/authorize', window.location.origin);
    url.searchParams.set('authorizationToken', e.data.authorizationToken);
    url.searchParams.set('location', window.location.href.substring(window.location.origin.length));
    window.location = url.toString();
  }
}, false);

document.getElementById('login-button').addEventListener('click', loginClicked);

styles:
* {
  box-sizing: border-box;
}

html, body {
  margin: 0;
  padding: 0;
  font-family: "Benton Sans", Helvetica, Sans-serif;
  font-size: 16px;
  line-height: 160%;
  width: 100%;
  height: 100%;
}

.container {
  width: 100%;
  height: 100%;
  display: flex;
  padding: 100px;
}

.info {
  max-width: 370px;
  z-index: 1;
  position: relative;
}

h1 {
  margin: 0;
  font-size: 40px;
  line-height: 130%;
  font-weight: bold;
}

a {
  color: #000;
}

.decorative-image {
  position: absolute;
  right: 80px;
  bottom: 80px;
  width: 50vw;
  max-width: 1000px;
}

@media(max-width: 620px) {
  .container {
    padding: 40px;
  }

  .decorative-image {
    right: 20px;
    bottom: 20px;
    width: 80vw;
  }
}

.button-wrap {
  display: flex;
  flex-direction: row;
}

.button-wrap * + * {
  margin-left: 7px;
}

.button {
  border-radius: 5px;
  border: 2px solid black ;
  box-sizing: border-box;
  background-color: white;
  text-decoration: none;
  text-align: center;
  padding: 0.375em 0.5em 0.1875em;
  font-weight: 600;
  line-height: 1;
}
