styles:
body {
    margin: 0;
    padding: 0;
    font-family: "Nunito Sans", sans-serif;
    background-color: #f5f5f5;
}

.page-container {
    height: 100vh;
    width: 100vw;
    margin: 0;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: row;
    flex-wrap: wrap;
}

.page-info {
    height: auto;
    width: auto;
    margin-bottom: 25px;
}

.page-title {
    width: 100%;
}

.page-title p {
    text-align: center;
    font-size: 70px;
    color: #bdbdbd;
    font-weight: bold;
    letter-spacing: 10px;
    margin: 0 0 25px;
}

.page-description p {
    margin: 0;
    text-align: center;
    font-size: 30px;
    color: #454545;
    font-weight: bold;
    word-spacing: 5px;
}

.page-info-image {
    width: 100%;
    text-align: center;
}

.page-info-image img {
    width: 150px;
    height: 150px;
    text-align: center;
    margin-bottom: 10px;
}

.page-description {
    margin: 20px;
}

.install-promo {
    width: 100%;
    height: auto;
    padding-top: 10px;
    padding-bottom: 10px;
    position: fixed;
    display: none;
    align-items: center;
    background-color: #ffffff;
    bottom: 0;
    left: 0;
}

.install-promo img {
    width: 15%;
    height: 15%;
    margin-left: 10px;
    margin-right: 10px;
}

.install-promo-title {
    width: 100%;
    color: #212121;
    font-size: 20px;
}

.install-promo-description {
    width: 100%;
    text-align: left;
    margin-top: 3px;
    color: #424242;
    font-size: 12px;
}

.install-promo-button {
    width: 20%;
    height: auto;
    padding: 8px;
    margin-right: 10px;
    text-align: center;
    color: #212121;
    border-radius: 12px;
    border: 1px solid #1976d2;
    font-size: 16px;
}

.install-promo-button:active {
    background-color: #64b5f6;
}

@media only screen and (max-width: 600px) {
    .page-title p {
        font-size: 32px;
    }

    .page-description p {
        font-size: 25px;
    }

    .install-promo {
        display: flex;
    }
}
