styles:
* {
    padding: 0;
    margin: 0;
}

body,
html {
    font-family: "Proxima Nova", Arial, sans-serif;
    -webkit-font-smoothing: antialiased;
    font-size: 10px;
    color: #1d1f21;
    background-color: #fff;
    height: 100%;
    width: 100%;
    line-height: 1.25;
}

.main-container {
    padding: 8.4rem 15% 16rem 15%;
}

p {
    font-size: 1.8rem;
}

ul {
    padding-left: 1.8rem;
    margin-bottom: 2rem;
}

li {
    font-size: 1.8rem;
    margin-bottom: 1rem;
}

.logo {
    width: 9.7rem;
}

.headline-container {
    display: flex;
    flex-direction: row-reverse;
    justify-content: space-between;
}

.warning-img {
    width: 52rem;
    margin-right: -6rem;
    margin-top: -6rem;
}

.header,
.headline-container,
.content {
    margin-bottom: 4rem;
}

.headline-title,
.content-title,
.content-list {
    margin-bottom: 2rem;
}

.headline {
    width: 65rem;
    margin-right: 2rem;
}

.headline-title {
    font-weight: 800;
    font-size: 4.2rem;
}

.headline-subtitle {
    color: #56575b;
    font-size: 2.4rem;
    line-height: 3.2rem;
}

.content-title {
    font-weight: 800;
    font-size: 3.2rem;
}

.warning {
    color: #94261d;
    background-color: #fee8e7;
    padding: 1.5rem 2rem;
    border-radius: 0.6rem;
}

.text-bold {
    font-weight: bold;
}

#clickthrough {
    color: #94261d;
    overflow-wrap: anywhere;
}

@media (max-width: 1020px) {
    .main-container {
        padding: 7% 5% 14% 5%;
    }

    .header {
        margin-bottom: 2rem;
    }

    .headline-container {
        flex-direction: column;
        justify-content: center;
    }

    .headline {
        width: 100%;
    }

    .warning-img {
        width: 50%;
        margin: 0 auto 2rem;
    }
}

@media (max-width: 750px) {
    .warning-img {
        width: 70%;
    }
}
